import React, { useEffect, useState } from "react";

import { push } from "connected-react-router";
import { useDispatch, useSelector } from "react-redux";
import { addFavorite, deleteFavorite } from "../../reducks/favorites/operations";
import { getFavorites } from "../../reducks/favorites/selectors";
import likeIcon from "../../assets/img/Like icon.png";
import heart from "../../assets/img/heart.svg";
const Grid = ({ place }) => {
  console.log(place);
  const dispatch = useDispatch();
  const clickFavorite = (place) => {
    dispatch(addFavorite(place));
  };
  const selector = useSelector((state) => state);
  const favorites = getFavorites(selector);
  const [showLikeButton, setShowLikeButton] = useState(true);
  useEffect(() => {
    let favoritePlace = favorites.filter((favorite) => favorite.id == place.id);
    if (favoritePlace.length > 0) {
      setShowLikeButton(false);
    }
  }, []);
  return (
    <div class="container">
      <img
        src={place.image}
        alt=""
        class="src"
        onClick={() => dispatch(push("/places/" + place.id))}
      />
      <h2>{place.name}</h2>
      <p>{place.description}</p>
      <div class="like">
        {/* <img src={likeIcon} alt="" class="src" onClick={() => clickFavorite(place)} /> */}
        {showLikeButton ? (
          <img
            src={likeIcon}
            onClick={() => {
              setShowLikeButton(!showLikeButton);
              clickFavorite(place);
            }}
            id="like"
          />
        ) : (
          <img
            src={heart}
            onClick={() => {
              setShowLikeButton(!showLikeButton);
              dispatch(deleteFavorite(place));
            }}
            id="heart"
          />
        )}
      </div>
    </div>
  );
};
export default Grid;
