import API from "../../API";
import { fetchPlaceAction } from "./actions";
import { getPlaces } from "./selectors";
const api = new API();
export const fetchPlaces = (search, category) => {
    return async (dispatch) => {
      return api
        .getPlaces(search, category)
        .then((places) => {
          dispatch(fetchPlaceAction(places));
        })
        .catch((error) => {
          alert("Failed to connect API: /places/");
        });
    };
  };
