import React, { useEffect,useState } from "react";

import { push } from "connected-react-router";
import { useDispatch, useSelector } from "react-redux";
import { addFavorite } from "../../reducks/favorites/operations";
import { getFavorites } from "../../reducks/favorites/selectors";
import likeIcon from "../../assets/img/Like icon.png"
const Grid = ({places}) => {
    const dispatch = useDispatch()
    const clickFavorite = (places) => {
        dispatch(addFavorite(places))
    }
    const selector = useSelector(state=>state)
    const favorites = getFavorites(selector)
    const [showLikeButton, setShowLikeButton] = useState(true)
    useEffect(() => {
        let favoritePlace = favorites.filter(
          (favorite) => favorite.id == places.id
        );
        if (favoritePlace.length > 0) {
          setShowLikeButton(false);
        }
      }, []);
      return(

            <div class="container">
                <img src={places.img} alt="" class="src" onClick={()=>dispatch(push("/places/"+place.id))}/>
                <h2>{place.name}</h2>
                <p>{place.description}</p>
                <div class="like">
                    <img src={likeIcon} alt="" class="src"/>
                </div>

            </div>
      )

} 
export default Grid
